GO
ALTER VIEW [dbo].[MRX_CONNECTIONS]
(CONN_ID, CONN_NAME, CONN_TYPE) AS
SELECT PID, NAME,
CASE WHEN DISCRIMINATOR = 'JDBCCONNECTINFO' THEN 'JDBC' 
WHEN DISCRIMINATOR = 'URLLOCATION' THEN 'URL' 
WHEN DISCRIMINATOR = 'WEBSERVICECONNECTINFO' THEN 'WEB SERVICE' 
WHEN DISCRIMINATOR = 'NRDBMETACONNECTINFO' THEN 'NRDB META' 
WHEN DISCRIMINATOR = 'NRDBNATIVECONNECTINFO' THEN 'NRDB COMMON' 
WHEN DISCRIMINATOR = 'DB2NATIVECONNECTINFO' THEN 'DB2' 
WHEN DISCRIMINATOR = 'DB2INATIVECONNECTINFO' THEN 'DB2 I5OS' 
WHEN DISCRIMINATOR = 'DB2ZNATIVECONNECTINFO' THEN 'DB2 ZOS' 
WHEN DISCRIMINATOR = 'ODBCNATIVECONNECTINFO' THEN 'ODBC' 
WHEN DISCRIMINATOR = 'ORACLENATIVECONNECTINFO' THEN 'ORACLE' 
WHEN DISCRIMINATOR = 'PWXMETACONNECTINFO' THEN 'PWX META' 
WHEN DISCRIMINATOR = 'SQLSERVERNATIVECONNECTINFO' THEN 'SQLSERVER' 
WHEN DISCRIMINATOR = 'SYBASENATIVECONNECTINFO' THEN 'SYBASE' 
WHEN DISCRIMINATOR = 'TERADATANATIVECONNECTINFO' THEN 'TERADATA' 
WHEN DISCRIMINATOR = 'SAPCONNECTINFO' THEN 'SAP' 
WHEN DISCRIMINATOR = 'SDKCONNECTINFO' THEN 'SDK' 
WHEN DISCRIMINATOR = 'IMSNATIVECONNECTINFO' THEN 'IMS' 
WHEN DISCRIMINATOR = 'SEQNATIVECONNECTINFO' THEN 'SEQ' 
WHEN DISCRIMINATOR = 'VSAMNATIVECONNECTINFO' THEN 'VSAM' ELSE
'OTHER' END 
FROM "dbo".MRI_CONNECT_INFO
GO

